import React, { useState } from "react";
import { CheckSquare,Circle, Clock, MoreHorizontal } from "react-feather";

import Dropdown from "../Dropdown/Dropdown";
import "./Card.css";
import CardInfo from "./CardInfo/CardInfo";




function Card(props) {
  const [showDropdown, setShowDropdown] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [showDetails, setShowDetails] = useState(false); // New state for additional details

  const { id, title, priority, status, tag, userId, date, tasks, labels } =
    props.card;

  const formatDate = (value) => {
    if (!value) return "";
    const date = new Date(value);
    if (!date) return "";

    const months = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ];

    const day = date.getDate();
    const month = months[date.getMonth()];
    return day + " " + month;
  };

  const handleFeatureRequest = () => {

    setShowModal(true);
  };

  const handleShowDetails = () => {
    setShowDetails(!showDetails);
  };

  return (
    <>
      {showModal && (
        <CardInfo
          onClose={() => setShowModal(false)}
          card={props.card}
          boardId={props.boardId}
          updateCard={props.updateCard}
        />
      )}

      <div
        className="card"
        draggable
        onDragEnd={() => props.dragEnded(props.boardId, id)}
        onDragEnter={() => props.dragEntered(props.boardId, id)}
      >
        <div className="card_top">
          <div className="card_top_labels">
            {labels?.map((item, index) => (
              <label key={index} style={{ backgroundColor: item.color }}>
                {item.text}
              </label>
            ))}
          </div>
          <div
            className="card_top_more"
            onClick={(event) => {
              event.stopPropagation();
              setShowDropdown(true);
            }}
          >
            <MoreHorizontal />
            {showDropdown && (
              <Dropdown
                class="board_dropdown"
                onClose={() => setShowDropdown(false)}
              >
                <p onClick={() => props.removeCard(props.boardId, id)}>
                  Delete Card
                </p>
              </Dropdown>
            )}
          </div>
        </div>
        <div className="card_title">{title}</div>
        <div className="card_buttons">
        <button onClick={handleShowDetails} style={{ margin: 20, background: 'white', borderRadius: '8px', padding: '8px', border: 'none' }}>
          <MoreHorizontal />
        </button>
        <button onClick={handleFeatureRequest} style={{ background: 'white', borderRadius: '8px', padding: '8px', border: 'none' }}>
          <Circle style={{ color: 'gray', fill: 'gray', fontSize: '1rem' }} />
           Feature Request
        </button>
      </div>

        {showDetails && (
          <div className="card_details">
            <p className="card_detail">Priority: {priority}</p>
            <p className="card_detail">Status: {status}</p>
            <p className="card_detail">Tag: {tag}</p>
            <p className="card_detail">User ID: {userId}</p>
          </div>
        )}
        <div className="card_footer">
          {date && (
            <p className="card_footer_item">
              <Clock className="card_footer_icon" />
              {formatDate(date)}
            </p>
          )}
          {tasks && tasks?.length > 0 && (
            <p className="card_footer_item">
              <CheckSquare className="card_footer_icon" />
              {tasks?.filter((item) => item.completed)?.length}/{tasks?.length}
            </p>
          )}
        </div>
      </div>
    </>
  );
}

export default Card;
