import React, { useState } from "react";
import { X } from "react-feather";
import "./Editable.css";

function Editable(props) {
  const [isEditable, setIsEditable] = useState(false);
  const [inputText, setInputText] = useState(props.defaultValue || "");

  const submission = (e) => {
    e.preventDefault();
    if (inputText && props.onSubmit) {
      setInputText("");
      props.onSubmit(inputText);
    }
    setIsEditable(false);
  };

  const renderDisplay = () => {
    console.log(props.text)
    if (Array.isArray(props.text)) {
      let index;  // Declare index here

    return (
      <div className="editable_display_array">
        {props.text.map((item, i) => {
          index = i;  // Assign i to index
          return (
            <p
              key={i}
              className={`editable_display ${
                props.displayClass ? props.displayClass : ""
              }`}
              onClick={() => setIsEditable(true)}
            >
              {item}
            </p>
          );
        })}
      </div>
    );
    } else {
      return (
        <p
          className={`editable_display ${
            props.displayClass ? props.displayClass : ""
          }`}
          onClick={() => setIsEditable(true)}
        >
          {props.text}
        </p>
      );
    }
  };

  return (
    <div className="editable">
      {isEditable ? (
        <form
          className={`editable_edit ${props.editClass ? props.editClass : ""}`}
          onSubmit={submission}
        >
          {Array.isArray(props.text) ? (
            props.text.map((item, index) => (
              <input
                key={index}
                type="text"
                value={item}
                placeholder={props.placeholder || props.text}
                onChange={(event) => setInputText(event.target.value)}
                autoFocus
              />
            ))
          ) : (
            <input
              type="text"
              value={inputText}
              placeholder={props.placeholder || props.text}
              onChange={(event) => setInputText(event.target.value)}
              autoFocus
            />
          )}
          <div className="editable_edit_footer">
            <button type="submit">{props.buttonText || "Add"}</button>
            <X onClick={() => setIsEditable(false)} className="closeIcon" />
          </div>
        </form>
      ) : (
        renderDisplay()
      )}
    </div>
  );
}

export default Editable;
