import React, { useEffect, useState } from "react";
import { fetchData } from './utils';

import Board from "./Components/Board/Board";

import "./App.css";
import Editable from "./Components/Editabled/Editable";
import Edit from "./Components/Editabled/Edit";




function App() {

  const [boards, setBoards] = useState(
    JSON.parse(localStorage.getItem("prac-kanban")) || []
  );
  const board=[ 'Backlog', 'Todo', 'In progress', 'Done', 'Cancelled']

  const [targetCard, setTargetCard] = useState({
    bid: "",
    cid: "",
  });
  const [tickets, setTickets] = useState([]);
  const [users, setUsers] = useState([]);
 const [groupedUserTickets, setGroupedUserTickets] = useState({
  'Backlog': [],
  'Todo': [],
  'In progress': [],
  'Done': [],
  'Cancelled': [],
});

  useEffect(() => {
    fetchData().then(({ tickets, users }) => {
      console.log("Tickets:", tickets);
      console.log("Users:", users);
      setTickets(tickets);
      setUsers(users);

      // Group tickets by user
      const groupedTicketsByUser = tickets.reduce((grouped, ticket) => {
        const status = ticket.status;
        if (!grouped[status]) {
          grouped[status] = [];
        }
        grouped[status].push(ticket);
        return grouped;
      }, {});
      setGroupedUserTickets(groupedTicketsByUser);
    });
  }, []);

  const addboardHandler = (name) => {
    const tempBoards = [...boards];
    tempBoards.push({
      id: Date.now() + Math.random() * 2,
      title: name,
      cards: [],
    });
    setBoards(tempBoards);
  };


  const removeBoard = (id) => {
    const index = boards.findIndex((item) => item.id === id);
    if (index < 0) return;

    const tempBoards = [...boards];
    tempBoards.splice(index, 1);
    setBoards(tempBoards);
  };

  const addCardHandler = (id, title) => {
    const index = boards.findIndex((item) => item.id === id);
    if (index < 0) return;

    const tempBoards = [...boards];
    tempBoards[index].cards.push({
      id: Date.now() + Math.random() * 2,
      title,
      labels: [],
      date: "",
      tasks: [],
    });
    setBoards(tempBoards);
  };

  const removeCard = (bid, cid) => {
    const index = boards.findIndex((item) => item.id === bid);
    if (index < 0) return;

    const tempBoards = [...boards];
    const cards = tempBoards[index].cards;

    const cardIndex = cards.findIndex((item) => item.id === cid);
    if (cardIndex < 0) return;

    cards.splice(cardIndex, 1);
    setBoards(tempBoards);
  };

  const dragEnded = (bid, cid) => {
    let s_boardIndex, s_cardIndex, t_boardIndex, t_cardIndex;
    s_boardIndex = boards.findIndex((item) => item.id === bid);
    if (s_boardIndex < 0) return;

    s_cardIndex = boards[s_boardIndex]?.cards?.findIndex(
      (item) => item.id === cid
    );
    if (s_cardIndex < 0) return;

    t_boardIndex = boards.findIndex((item) => item.id === targetCard.bid);
    if (t_boardIndex < 0) return;

    t_cardIndex = boards[t_boardIndex]?.cards?.findIndex(
      (item) => item.id === targetCard.cid
    );
    if (t_cardIndex < 0) return;

    const tempBoards = [...boards];
    const sourceCard = tempBoards[s_boardIndex].cards[s_cardIndex];
    tempBoards[s_boardIndex].cards.splice(s_cardIndex, 1);
    tempBoards[t_boardIndex].cards.splice(t_cardIndex, 0, sourceCard);
    setBoards(tempBoards);

    setTargetCard({
      bid: "",
      cid: "",
    });
  };
    const renderBoard = (item) => (
      <Board
        key={item.id}
        board={item}
        addCard={addCardHandler}
        removeBoard={() => removeBoard(item.id)}
        removeCard={removeCard}
        dragEnded={dragEnded}
        dragEntered={dragEntered}
        updateCard={updateCard}
      />
    );

    const renderGroupedBoard = (boardKey) => (
      <Board
        key={boardKey}
        board={{
          id: boardKey,
          title: boardKey,
          cards: groupedUserTickets[boardKey],
        }}
        addCard={addCardHandler}
        removeBoard={() => removeBoard(boardKey)}
        removeCard={removeCard}
        dragEnded={dragEnded}
        dragEntered={dragEntered}
        updateCard={updateCard}
      />
    );

    const renderEditable = (boardName, index) => (
      <Editable
        key={index}
        displayClass={`app_boards_add-board ${boardName.toLowerCase()}`}
        editClass={`app_boards_add-board_edit ${boardName.toLowerCase()}`}
        placeholder={`Enter ${boardName} Name`}
        text={boardName}
        buttonText={boardName}
        onSubmit={(name) => addBoardAndDisplay(name, boardName)}
      />
    );

    const addBoardAndDisplay = (name, boardName) => {
      const tempBoards = [...boards];
      tempBoards.push({
        id: Date.now() + Math.random() * 2,
        title: name,
        cards: groupedUserTickets[boardName] || [],
      });
      setBoards(tempBoards);
    };

  const dragEntered = (bid, cid) => {
    if (targetCard.cid === cid) return;
    setTargetCard({
      bid,
      cid,
    });
  };

  const updateCard = (bid, cid, card) => {
    const index = boards.findIndex((item) => item.id === bid);
    if (index < 0) return;

    const tempBoards = [...boards];
    const cards = tempBoards[index].cards;

    const cardIndex = cards.findIndex((item) => item.id === cid);
    if (cardIndex < 0) return;

    tempBoards[index].cards[cardIndex] = card;

    setBoards(tempBoards);
  };

  useEffect(() => {
    localStorage.setItem("prac-kanban", JSON.stringify(boards));
  }, [boards]);

  return (
    <div className="app">
      <div className="app_nav">
        <h1>Kanban Board</h1>
        <h2>lasd</h2>
        {/* Display tickets and users data here */}
        <div>
          <div>
            <h3>Users:</h3>
            <pre>{JSON.stringify(users, null, 2)}</pre>
          </div>
          <div>
            <h3>Grouped Tickets by User:</h3>
            <pre>{JSON.stringify(groupedUserTickets, null, 2)}</pre>
          </div>
        </div>
        <div className="app_boards_container">
          <div className="app_boards">
            {boards.map(renderBoard)}
            {Object.keys(groupedUserTickets).map(renderGroupedBoard)}
            <div className="app_boards_last">
              {board.map((boardName, index) => renderEditable(boardName, index))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
