export const groupAndSortTickets = (data, groupBy, sortBy) => {
    if (!data || !Array.isArray(data)) {
      return [];
    }
  
    // Grouping
    let groupedData = [];
    if (groupBy === "status") {
      groupedData = groupByStatus(data);
    } else if (groupBy === "user") {
      groupedData = groupByUser(data);
    } else if (groupBy === "priority") {
      groupedData = groupByPriority(data);
    }
  
    // Sorting
    if (sortBy === "priority") {
      groupedData = sortByPriority(groupedData);
    } else if (sortBy === "title") {
      groupedData = sortByTitle(groupedData);
    }
  
    return groupedData;
  };
  
  const groupByStatus = (data) => {
    const groupedData = {};
    data.forEach((ticket) => {
      const status = ticket.status || "No Status";
      if (!groupedData[status]) {
        groupedData[status] = [];
      }
      groupedData[status].push(ticket);
    });
    return convertObjectToArray(groupedData);
  };
  
  const groupByUser = (data) => {
    const groupedData = {};
    data.forEach((ticket) => {
      const user = ticket.user || "No User";
      if (!groupedData[user]) {
        groupedData[user] = [];
      }
      groupedData[user].push(ticket);
    });
    return convertObjectToArray(groupedData);
  };
  
  const groupByPriority = (data) => {
    const groupedData = {};
    data.forEach((ticket) => {
      const priority = ticket.priority || "No Priority";
      if (!groupedData[priority]) {
        groupedData[priority] = [];
      }
      groupedData[priority].push(ticket);
    });
    return convertObjectToArray(groupedData);
  };
  
  const sortByPriority = (groupedData) => {
    return groupedData.map((group) => {
      group.items.sort((a, b) => b.priority - a.priority);
      return group;
    });
  };
  
  const sortByTitle = (groupedData) => {
    return groupedData.map((group) => {
      group.items.sort((a, b) => a.title.localeCompare(b.title));
      return group;
    });
  };
  
  const convertObjectToArray = (object) => {
    return Object.keys(object).map((key) => ({
      group: key,
      items: object[key],
    }));
  };
